    public partial class frmNhanVienn : DevExpress.XtraEditors.XtraForm
    {
        NhanVienCtr nvCtr = new NhanVienCtr();
        private int flagLuu = 0;
        public frmNhanVienn()
        {
            InitializeComponent();



